INSERT INTO public.points_translations (point_id, language, name, description) VALUES ('cfc3e99e-2f75-11e8-b8c1-fbbf8ac04af7', 'en', 'остров Горелый - "Петроглифы Канозера"', 'Остров Горелый, группа-2. Петроглифы расположены на протяжении 100 м в юго-западной части острова, на южной оконечности линии береговых скал: 14 фигур в 4-х группах. Наибольшее расстояние от групп петроглифов до уреза воды 4 м.
INSERT INTO public.points_translations (point_id, language, name, description) VALUES ('cfc42292-2f75-11e8-b8c1-d3e2c15b5527', 'en', 'остров Еловый - "Петроглифы Канозера"', 'Скалы идут узкой полосой по северо-западному берегу. Петроглифы расположены на всем его протяжении: 279 фигур в 6-ти группах, причем одна группа (№ 6) на скате, обращенном внутрь острова. Наибольшее расстояние от групп петроглифов до уреза воды 19 м.');
INSERT INTO public.points_translations (point_id, language, name, description) VALUES ('cfc44736-2f75-11e8-b8c1-738bdf3be664', 'en', 'остров Каменный - "Петроглифы Канозера"', 'Скальные выходы сосредоточены в северной и западной частях острова. Петроглифы расположены в западной части острова: 669 фигур в 7-ми группах. Наибольшее расстояние от групп петроглифов до уреза воды 33 м.');
