INSERT INTO public.points_translations (point_id, language, name, description) VALUES ('cfc683c0-2f75-11e8-b8c1-7fe5d0faa3e2', 'en', 'Маманя (большой янискенгас)', '5/6 к.с. - самая правильная линия - это обнос по левому берегу! Спускаемся в низ под водопад созерцаем мощь и величие, готовимся страховать созревших прыгать. Для тех кто созрел - центральная линия, чуть правее центра вода течет по каменной горке, в самом конце этой горочки есть небольшой кикерочек, сильно стараемся туда попасть и забуфить с кикера, приземляемся в кипящую чашу и подгребаем из нее в сторону "выхода" - каменной горки, стараемся держать нос лодки или корму четко по течению. Крайне рекомендуется исполнять все на ровном киле!). ВНИМАНИЕ !!! Обязательно стоит убедиться что в самой нижней чашке нет никаких посторонних предметов (тросов по центру)!!');
