INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d11e1666-2f75-11e8-b8c1-a79265883090', 'en', 'Lower', '{}', '', 'After rain', '1-3 days after rain', '2018-03-24 15:12:50.089742+00', '2018-03-24 15:12:50.089742+00');
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d11ee974-2f75-11e8-b8c1-e3c6ede5f240', 'en', 'Lower', '{}', 'If you feel like you are comfortable at the last big rapid at the take out then just go to the put in. It''s a consistent clacc V. There are some big drops in the beginning and it''s hard to stop between them. It''s not a very clean river and it''s really demanding and special.
