INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1c57bc2-2f75-11e8-b8c1-9f59685e8ae2', 'en', 'Умба', '{}', 'отличный матрасный недельный отдых (по настоящему синий маршрут) с хорошей рыбалкой комарами и видами. иногда на стоянках приходят медведи и отжимают еду!
