INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1dcd600-2f75-11e8-b8c1-e71ed5553f5e', 'en', 'Lower', '{}', 'It''s a nice creek with countless technical slides with water.
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1ddab20-2f75-11e8-b8c1-9f1b3c16356a', 'en', 'Lower', '{}', 'If your are not good enough for "long race" section, but you want to run something more then just a qualification section, you can continue down next 5-6 km on easy class II-III sections
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1de840a-2f75-11e8-b8c1-5b22af123b0e', 'en', 'Upper', '{}', 'Участок состоит из трех ущелий. Нижнее можно просмотреть с левого берега реки с асфальтовой дороги на Бахмаро, среднее только небольшой кусочек с большого расстояния при заброске. Сплав можно начать в зависимости от уровня воды перед любым из ущелий, чем выше, тем больше требуется воды. Характер – постоянное равномерное падение с минимумом суводей и большой скоростью течения. Русло на всем протяжении сильно завалено большими камнями. Береговая обстановка в целом благоприятна, для просмотра и обноса в большинстве своем можно использовать оба берега. Выход с реки из ущелий очень затруднен.
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1df5ec0-2f75-11e8-b8c1-87f99571c7bb', 'en', 'Middle', '{}', 'Секцию можно поделить на 2 участка . Первый участок от старта до моста на километр ниже нового моста на завод Набеглави. Нон-стопшивера с хорошим падением без выраженных порогов. Второй участок начинается поворотом реки направо и бетонной стенкой дороги справа. Несколько выраженных порогов со сливами до метра и бочками, между порогами быстроток с уловами. Дорога идёт вплотную к реке и всё можно просмотреть по пути наверх. Второй участок выделяется, если на первом покажется много, то перед вторым лучше вылезти.', '', '', '2018-03-24 15:12:51.3567+00', '2018-03-24 15:12:51.3567+00');
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1e02bfc-2f75-11e8-b8c1-0388d017fe8e', 'en', 'Middle Toachi', '{}', '', '', 'good low water run goes well up to 12 on gage @putin', '2018-03-24 15:12:51.361567+00', '2018-03-24 15:12:51.361567+00');
