INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d2008ce4-2f75-11e8-b8c1-773602da3d2e', 'en', 'Шуонийоки', '{}', 'У границы с Норвегией бодрый дабл-дроп (8м) - находится в левой протоке.', 'середина мая по сентябрь', '', '2018-03-24 15:12:51.573951+00', '2018-03-24 15:12:51.573951+00');
