INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d2188f24-2f75-11e8-b8c1-6fc34f6f6557', 'en', 'Section A (classic)', '{}', 'The river has its source in the midst of mountain peaks of more than 3000 m and its classic section can be paddled from the beginning of the thaw.
