INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d2200c7c-2f75-11e8-b8c1-13bf7b3eb654', 'en', 'Main section', '{}', 'Because of the numerous barages this river is only viable during water releases in dry periods in mid May to mid June or after very heavy rain.
