INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d2225342-2f75-11e8-b8c1-dbfc1d94e7c4', 'en', 'Section A', '{}', '**Put in**: from Oloron go up the valley of the Aspe via the N134.  Five kolometres after the village of Urdos there is a lake section of the river below, which you can reach via a sloping field.
