INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d226ebc8-2f75-11e8-b8c1-9378ffd9be90', 'en', 'Водопады', '{}', 'Каньон с тремя водопадами. Насколько нам известно, первый водопад до сих пор не был пройден (2018г).
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d2284a2c-2f75-11e8-b8c1-5fb05a83e763', 'en', 'от Белиза до устья', '{}', 'Для пребывания на территории ГБАО необходим специальный пропуск. Оформлением пропуска могут заняться турфирмы Киргизии и Таджикистана. Оформление пропуска - это довольно длительная процедура, поэтому паспортные данные для пропуска нужно предоставить за 30-40 дней до путешествия.
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d22a1b22-2f75-11e8-b8c1-07f9879a5a38', 'en', 'Плесы', '{}', 'Для пребывания на территории ГБАО необходим специальный пропуск. Оформлением пропуска могут заняться турфирмы Киргизии и Таджикистана. Оформление пропуска - это довольно длительная процедура, поэтому паспортные данные для пропуска нужно предоставить за 30-40 дней до путешествия.', 'Пока не слишком холодно', '', '2018-03-24 15:12:51.846981+00', '2018-03-24 15:12:51.846981+00');
