INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d251c190-2f75-11e8-b8c1-0b1f39b3360a', 'en', 'O Casal', '{}', 'It''s a short creek run with some technical rapids.
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d2343832-2f75-11e8-b8c1-77fd0f7a2300', 'en', 'Koppentraun', '{}', 'Its said to be one of the best runs of the Austrian Alps. From end of April to beginning of June (during snow melt) and after rain it’s a paradise for big volume boating at the Northern side of the beautiful Dachstein mountain. It sets the border between Upper Austria and Styria and floats from Bad Aussee to Obertran into Hallstättersee.
