INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d2477e74-2f75-11e8-b8c1-dbff99a7e272', 'en', 'Среднее', '{}', 'Обихингоу - это классическая река Памира, открытая очень давно, и популярная в узких кругах. Это река с огромным расходом и мощными порогами с выраженным перепадом. Настоящий рай для любителей бигвольюма! Только ради этой реки уже стоит побывать в Таджикистане.
