INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1730838-2f75-11e8-b8c1-d7e2dd9cb216', 'en', 'Classic', '{}', '', '', '0-7 days after rainfall', '2018-03-24 15:12:50.646309+00', '2018-03-24 15:12:50.646309+00');
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d173cb4c-2f75-11e8-b8c1-273fe1961244', 'en', 'Highway run', '{}', 'Varzob is an alpine-style steep creek with the road always by the river. Everything is very easy to scout and to portage.
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d174aea4-2f75-11e8-b8c1-67603f6c9ac9', 'en', 'Lower', '{}', 'This section is less steep than the upper one so it cant be paddled with much more water. Its a run between restaurants and rest areas. You are the main attraction!
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1758d1a-2f75-11e8-b8c1-d3e10aa53b20', 'en', 'The Canyons', '{}', 'Muksu is an outstanding multi-day trip in the heart of Pamir mountains. Its a very remote and very demanding river full of class V must-runs in deep canyons. If the road to the put in is in a good state and the water level is low enough you can paddle the river in 4-5 days. There were just few groups of kayakers here and around 10 groups of Russian catarafters. Some of catarafting groups had fatal accidents here. At certain flows the river can be paddled without portages in a kayak.
