INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1b3b9c8-2f75-11e8-b8c1-ab8d0adeadb0', 'en', 'School run', '{}', 'It''s a nice run for beginners where most of the local kayakers learned to paddle. There are couple waves to play and an easy artificial weir.', 'After rain', '', '2018-03-24 15:12:51.070814+00', '2018-03-24 15:12:51.070814+00');
