INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1be0ad6-2f75-11e8-b8c1-236997dad6ae', 'en', 'Middle', '{}', '', '', '', '2018-03-24 15:12:51.138651+00', '2018-03-24 15:12:51.138651+00');
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1befd92-2f75-11e8-b8c1-2f78d9c61163', 'en', 'Upper', '{}', 'The Upper Anzu will deliver, all the way from the lowest flows around the Tena area to a healthy high brown flow from all night rain storm, one of the most classic sections of whitewater in Ecuador. Understimated due to the distance and conditions of the shuttle, but as 2018 the road to the put in is almost asphalted to the end of the road , facilitating the access and make it easier. Combine with a Piatua lap to make the drive worth it all the way here, las cabaas de DON NAPO at the take out on the river right just upstream of the confluence with the Pitua are a great spot to stay or the cabaas Piatua. Food and beer under request.
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1c03d6a-2f75-11e8-b8c1-7bd0e5d84815', 'en', 'Dam run', '{}', 'It''s hard to get the proper flow here. The gauge is far downstream after all the dams. The actual water level in this section of the river can be very different even with the same reading at the gauge. Also because of the dams the water level can be different at different parts of this run. Usually it has to be more than 90 cumecs at the gauge. It can also be too high!
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1c1aa1a-2f75-11e8-b8c1-3339557cccdc', 'en', 'Lower', '{}', 'This section has more water than the one above because of the water release. The main difficulties are in the beginning. It has been paddled during flood.', 'Heavy rain', '', '2018-03-24 15:12:51.162144+00', '2018-03-24 15:12:51.162144+00');
