INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1d763fa-2f75-11e8-b8c1-835d017df098', 'en', 'Train section', '{}', 'It''s a really nice easy section. It flows in a remote canyon with no signs of civilization on the banks apart from monstrous highway and railway bridges. Be careful with trees and branches in the water at high flows.
INSERT INTO public.sections_translations (section_id, language, name, alt_names, description, season, flows_text, created_at, updated_at) VALUES ('d1d85bca-2f75-11e8-b8c1-4329e05694d1', 'en', 'Upper', '{}', 'This section is steeper than the one above and less continuous than the one below. It provides some great class III (III+) slides and a runnable dam. There is also a harder rapid with a nasty syphon on the right. It''s marked on the map of the section. Scout it and maybe portage it. It can also be full of wood.', 'After rain', '', '2018-03-24 15:12:51.318767+00', '2018-03-24 15:12:51.318767+00');
